	@Override
	public void setData() {
		setAmb(new float[]{0.0f, .0f, 0.0f});
		setDiff(new float[]{0.64f, .402f, 0.0f});
		setSpec(new float[]{0.75f, 0.75f, 0.75f});
		setCoords(GoldenShipCoords);
		setNormals(GoldenShipNormals);
		setTransparency(1.0f);
		setShine(20);
	}
	
	public void updateSpeed() {
		if(timeInBoost < 36) {
			timeInBoost ++;
			setSpeed(.15f);
			transList.set(5, new Transformation(10*timeInBoost, 0, 0, 1));
			setAmb(new float[]{1.0f, 0.8f, 0.2f});
			inProgress = true;
		}
		else {
			setSpeed(.04f);
			timeInBoost = 0;
			setAmb(new float[]{0.0f, .0f, 0.0f});
			inProgress = false;
		}
	}
	
	public boolean boostInProgress() {
		return inProgress;
	}

